import React, { useState, useEffect } from 'react';
import { 
  Users, 
  Upload, 
  Brain, 
  FileText, 
  Settings, 
  LogOut,
  Plus,
  Search,
  Eye,
  Edit,
  Download,
  AlertCircle,
  CheckCircle,
  Clock,
  Activity
} from 'lucide-react';

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState('patients');
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [currentReport, setCurrentReport] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isGeneratingReport, setIsGeneratingReport] = useState(false);
  const [isSavingReport, setIsSavingReport] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  // Mock data for demonstration
  const [mockPatients] = useState([
    {
      id: '1',
      patient_id: 'PT001',
      name: 'John Doe',
      age: 45,
      gender: 'Male',
      medical_history: 'Hypertension, Diabetes Type 2',
      created_at: '2024-01-15',
      images: [
        {
          id: 'img1',
          image_url: 'https://via.placeholder.com/400x300?text=Chest+X-Ray',
          image_type: 'xray',
          upload_date: '2024-01-15',
          findings: [
            {
              label: 'Normal lung fields',
              confidence: 0.92,
              description: 'Both lung fields show normal aeration'
            },
            {
              label: 'Cardiac silhouette normal',
              confidence: 0.88,
              description: 'Heart size within normal limits'
            }
          ],
          annotations: [
            {
              coordinates: [100, 100, 150, 120],
              label: 'Normal cardiac silhouette',
              confidence: 0.88
            }
          ]
        }
      ],
      reports: [
        {
          id: 'report1',
          status: 'draft',
          created_at: '2024-01-15',
          sections: {
            clinical_indication: 'Routine chest X-ray',
            technique: 'PA and lateral chest views',
            findings: 'Normal lung fields with high confidence. Cardiac silhouette normal with high confidence.',
            impression: 'Normal chest X-ray. No acute cardiopulmonary process identified.',
            recommendations: 'No immediate action required. Routine follow-up as indicated.'
          },
          literature_citations: [
            {
              title: 'Radiological findings in pulmonary consolidation: A systematic review',
              authors: ['Smith, J.', 'Johnson, A.', 'Brown, K.'],
              journal: 'Journal of Medical Imaging',
              year: 2023,
              relevance_score: 8
            }
          ]
        }
      ]
    }
  ]);

  // Initialize dashboard
  useEffect(() => {
    setPatients(mockPatients);
    setLoading(false);
  }, []);

  const handlePatientSelect = (patient) => {
    setSelectedPatient(patient);
    setActiveTab('images');
  };

  const handleImageSelect = (image) => {
    setSelectedImage(image);
    setActiveTab('analysis');
  };

  const handleAnalyzeImage = async () => {
    if (!selectedImage) return;

    setIsAnalyzing(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 3000));

      // Update image with new findings
      const updatedImage = {
        ...selectedImage,
        findings: [
          ...selectedImage.findings,
          {
            label: 'AI-detected subtle opacity',
            confidence: 0.73,
            description: 'Subtle opacity noted - requires clinical correlation'
          }
        ],
        annotations: [
          ...selectedImage.annotations,
          {
            coordinates: [200, 150, 80, 60],
            label: 'AI-detected subtle opacity',
            confidence: 0.73
          }
        ]
      };

      setSelectedImage(updatedImage);

      // Update patient data
      const updatedPatients = patients.map(p => {
        if (p.id === selectedPatient.id) {
          return {
            ...p,
            images: p.images.map(img => 
              img.id === selectedImage.id ? updatedImage : img
            )
          };
        }
        return p;
      });
      setPatients(updatedPatients);
      setSelectedPatient(updatedPatients.find(p => p.id === selectedPatient.id));

    } catch (error) {
      console.error('Analysis failed:', error);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleGenerateReport = async () => {
    if (!selectedImage) return;

    setIsGeneratingReport(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      const newReport = {
        id: `report_${Date.now()}`,
        status: 'draft',
        created_at: new Date().toISOString().split('T')[0],
        sections: {
          clinical_indication: 'Educational case study - AI-assisted analysis',
          technique: 'Standard imaging protocol with AI enhancement',
          findings: selectedImage.findings.map(f => 
            `${f.label} noted with ${Math.round(f.confidence * 100)}% confidence`
          ).join('. '),
          impression: 'Findings consistent with educational simulation. AI analysis suggests careful review of highlighted areas.',
          recommendations: [
            'Correlation with clinical history and symptoms recommended',
            'Consider additional imaging if clinically indicated', 
            'Review by qualified radiologist required for final interpretation'
          ]
        },
        literature_citations: [
          {
            title: 'AI-assisted diagnosis in medical imaging: Current applications',
            authors: ['Chen, L.', 'Garcia, M.', 'Taylor, S.'],
            journal: 'AI in Medicine',
            year: 2023,
            relevance_score: 9
          }
        ]
      };

      setCurrentReport(newReport);
      setActiveTab('report');

    } catch (error) {
      console.error('Report generation failed:', error);
    } finally {
      setIsGeneratingReport(false);
    }
  };

  const handleSaveReport = async (finalReport) => {
    setIsSavingReport(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Update report status
      const updatedReport = {
        ...currentReport,
        status: 'final',
        final_report: finalReport,
        saved_at: new Date().toISOString()
      };

      setCurrentReport(updatedReport);

      // Update patient data
      const updatedPatients = patients.map(p => {
        if (p.id === selectedPatient.id) {
          return {
            ...p,
            reports: [...p.reports, updatedReport]
          };
        }
        return p;
      });

      setPatients(updatedPatients);
      setSelectedPatient(updatedPatients.find(p => p.id === selectedPatient.id));

    } catch (error) {
      console.error('Save failed:', error);
    } finally {
      setIsSavingReport(false);
    }
  };

  const filteredPatients = patients.filter(patient =>
    patient.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    patient.patient_id.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  return (
