        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <Brain className="h-8 w-8 text-blue-600 mr-3" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Medical Imaging Assistant</h1>
                <p className="text-sm text-gray-500">Educational AI-Powered Platform</p>
              </div>
            </div>

            <div className="flex items-center gap-4">
              <span className="text-sm text-gray-600">Welcome, Student</span>
              <button className="p-2 text-gray-400 hover:text-gray-600">
                <Settings size={20} />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600">
                <LogOut size={20} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Navigation Tabs */}
        <div className="border-b border-gray-200 mb-6">
          <nav className="-mb-px flex space-x-8">
            <button
              onClick={() => setActiveTab('patients')}
              className={`py-2 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'patients'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <Users className="inline mr-2" size={16} />
              Patients ({patients.length})
            </button>

            {selectedPatient && (
              <>
                <button
                  onClick={() => setActiveTab('images')}
                  className={`py-2 px-1 border-b-2 font-medium text-sm ${
                    activeTab === 'images'
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <Eye className="inline mr-2" size={16} />
                  Images
                </button>

                <button
                  onClick={() => setActiveTab('analysis')}
                  className={`py-2 px-1 border-b-2 font-medium text-sm ${
                    activeTab === 'analysis'
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <Brain className="inline mr-2" size={16} />
                  Analysis
                </button>

                <button
                  onClick={() => setActiveTab('report')}
                  className={`py-2 px-1 border-b-2 font-medium text-sm ${
                    activeTab === 'report'
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <FileText className="inline mr-2" size={16} />
                  Reports
                </button>
              </>
            )}
          </nav>
        </div>

        {/* Content Area */}
        <div className="bg-white rounded-lg shadow">
          {activeTab === 'patients' && (
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-lg font-semibold">Patient Management</h2>
                <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center gap-2">
                  <Plus size={16} />
                  Add Patient
                </button>
              </div>

              {/* Search */}
              <div className="mb-6">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                  <input
                    type="text"
                    placeholder="Search patients by name or ID..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 pr-4 py-2 w-full border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  />
                </div>
              </div>

              {/* Patient List */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filteredPatients.map((patient) => (
                  <div
                    key={patient.id}
                    onClick={() => handlePatientSelect(patient)}
                    className="border border-gray-200 rounded-lg p-4 hover:shadow-md cursor-pointer transition"
                  >
                    <div className="flex justify-between items-start mb-2">
                      <h3 className="font-semibold text-gray-900">{patient.name}</h3>
                      <span className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded">
                        {patient.patient_id}
                      </span>
                    </div>

                    <div className="text-sm text-gray-600 space-y-1">
                      <p>Age: {patient.age} | {patient.gender}</p>
                      <p>Images: {patient.images.length}</p>
                      <p>Reports: {patient.reports.length}</p>
                    </div>

                    <div className="mt-3 flex items-center gap-2">
                      <span className={`text-xs px-2 py-1 rounded-full ${
                        patient.reports.some(r => r.status === 'final') 
                          ? 'bg-green-100 text-green-700'
                          : 'bg-yellow-100 text-yellow-700'
                      }`}>
                        {patient.reports.some(r => r.status === 'final') ? 'Completed' : 'In Progress'}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'images' && selectedPatient && (
            <div className="p-6">
              <div className="mb-6">
                <h2 className="text-lg font-semibold mb-2">
                  Images for {selectedPatient.name}
                </h2>
                <p className="text-sm text-gray-600">
                  Patient ID: {selectedPatient.patient_id} | Age: {selectedPatient.age} | {selectedPatient.gender}
                </p>
              </div>

              <div className="flex gap-6">
                {/* Image List */}
                <div className="w-1/3">
                  <button className="w-full mb-4 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center gap-2 justify-center">
                    <Upload size={16} />
                    Upload New Image
                  </button>

                  <div className="space-y-3">
                    {selectedPatient.images.map((image) => (
                      <div
                        key={image.id}
                        onClick={() => handleImageSelect(image)}
                        className={`border rounded-lg p-3 cursor-pointer transition ${
                          selectedImage?.id === image.id 
                            ? 'border-blue-500 bg-blue-50' 
                            : 'border-gray-200 hover:border-gray-300'
                        }`}
                      >
                        <img
                          src={image.image_url}
                          alt="Medical scan"
                          className="w-full h-24 object-cover rounded mb-2"
                        />
                        <div className="text-sm">
                          <p className="font-medium capitalize">{image.image_type}</p>
                          <p className="text-gray-500">{image.upload_date}</p>
                          <p className="text-xs text-green-600">
                            {image.findings.length} findings
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Image Viewer */}
                <div className="flex-1">
                  {selectedImage ? (
                    <div>
                      <div className="flex justify-between items-center mb-4">
                        <h3 className="font-semibold">
                          {selectedImage.image_type.toUpperCase()} - {selectedImage.upload_date}
                        </h3>
                        <div className="flex gap-2">
                          <button
                            onClick={handleAnalyzeImage}
                            disabled={isAnalyzing}
                            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 disabled:opacity-50 flex items-center gap-2"
                          >
                            <Brain size={16} />
                            {isAnalyzing ? 'Analyzing...' : 'AI Analyze'}
                          </button>
                          <button
                            onClick={handleGenerateReport}
                            disabled={isGeneratingReport}
                            className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 disabled:opacity-50 flex items-center gap-2"
                          >
                            <FileText size={16} />
                            {isGeneratingReport ? 'Generating...' : 'Generate Report'}
                          </button>
                        </div>
                      </div>

                      {/* Image Viewer Component would go here */}
                      <div className="border rounded-lg p-4 bg-gray-100 text-center">
                        <div className="aspect-video bg-black rounded flex items-center justify-center">
                          <p className="text-white">Image Viewer Component</p>
                        </div>

                        {/* Findings Summary */}
                        {selectedImage.findings.length > 0 && (
                          <div className="mt-4 p-4 bg-white rounded border">
                            <h4 className="font-semibold mb-2">AI Findings ({selectedImage.findings.length})</h4>
                            <div className="space-y-2">
                              {selectedImage.findings.map((finding, i) => (
                                <div key={i} className="flex justify-between items-center text-sm">
                                  <span>{finding.label}</span>
                                  <span className={`px-2 py-1 rounded text-xs ${
                                    finding.confidence > 0.8 
                                      ? 'bg-green-100 text-green-700'
                                      : finding.confidence > 0.6
                                      ? 'bg-yellow-100 text-yellow-700'
                                      : 'bg-red-100 text-red-700'
                                  }`}>
                                    {Math.round(finding.confidence * 100)}%
                                  </span>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  ) : (
                    <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
                      <Upload className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                      <p className="text-gray-500">Select an image to view</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {activeTab === 'analysis' && selectedImage && (
            <div className="p-6">
              <h2 className="text-lg font-semibold mb-6">AI Analysis Results</h2>

              {isAnalyzing ? (
                <div className="text-center py-12">
                  <div className="animate-spin w-12 h-12 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-4"></div>
                  <p className="text-lg font-medium">AI Analysis in Progress...</p>
                  <p className="text-sm text-gray-500">Processing image with computer vision models</p>
                </div>
              ) : (
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  {/* Image Viewer */}
                  <div>
                    <h3 className="font-semibold mb-4">Image with Annotations</h3>
                    <div className="border rounded-lg p-4 bg-gray-100 text-center">
                      <div className="aspect-video bg-black rounded flex items-center justify-center">
                        <p className="text-white">Advanced Viewer Component</p>
                      </div>
                    </div>
                  </div>

                  {/* Analysis Results */}
                  <div>
                    <h3 className="font-semibold mb-4">Analysis Summary</h3>
                    <div className="space-y-4">
                      {selectedImage.findings.map((finding, i) => (
                        <div key={i} className="border rounded-lg p-4">
                          <div className="flex justify-between items-start mb-2">
                            <h4 className="font-medium">{finding.label}</h4>
                            <span className={`px-2 py-1 rounded text-xs font-medium ${
                              finding.confidence > 0.8 
                                ? 'bg-green-100 text-green-700'
                                : finding.confidence > 0.6
                                ? 'bg-yellow-100 text-yellow-700'
                                : 'bg-red-100 text-red-700'
                            }`}>
                              {Math.round(finding.confidence * 100)}% confidence
                            </span>
                          </div>
                          {finding.description && (
                            <p className="text-sm text-gray-600">{finding.description}</p>
                          )}
                        </div>
                      ))}
                    </div>

                    {/* Action Buttons */}
                    <div className="mt-6 space-y-3">
                      <button
                        onClick={handleGenerateReport}
                        disabled={isGeneratingReport}
                        className="w-full bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 disabled:opacity-50 flex items-center gap-2 justify-center"
                      >
                        <FileText size={16} />
                        {isGeneratingReport ? 'Generating Report...' : 'Generate AI Report'}
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'report' && (
            <div className="p-6">
              <div className="mb-6">
                <h2 className="text-lg font-semibold">Report Editor</h2>
                <p className="text-sm text-gray-600">
                  Review and edit the AI-generated draft report
                </p>
              </div>

              {currentReport ? (
                <div className="space-y-6">
                  {/* Report Status */}
                  <div className="flex items-center gap-4 p-4 bg-gray-50 rounded-lg">
                    <div className="flex items-center gap-2">
                      {currentReport.status === 'final' ? (
                        <CheckCircle className="text-green-500" size={20} />
                      ) : (
                        <Clock className="text-yellow-500" size={20} />
                      )}
                      <span className="font-medium">
                        Status: {currentReport.status === 'final' ? 'Final' : 'Draft'}
                      </span>
                    </div>
                    <span className="text-sm text-gray-500">
                      Generated: {currentReport.created_at}
                    </span>
                  </div>

                  {/* Report Editor Component would go here */}
                  <div className="border rounded-lg p-6">
                    <h3 className="font-semibold mb-4">Report Content</h3>
                    <div className="bg-gray-50 p-4 rounded border">
                      <pre className="text-sm whitespace-pre-wrap">
                        {JSON.stringify(currentReport.sections, null, 2)}
                      </pre>
                    </div>

                    {/* Save Button */}
                    {currentReport.status !== 'final' && (
                      <div className="mt-4">
                        <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 flex items-center gap-2">
                          <Download size={16} />
                          Save Final Report
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              ) : (
                <div className="text-center py-12">
                  <FileText className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                  <p className="text-gray-500">No report generated yet</p>
                  <p className="text-sm text-gray-400">Generate a report from the Analysis tab</p>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Educational Disclaimer */}
        <div className="mt-6 bg-amber-50 border border-amber-200 rounded-lg p-4">
          <div className="flex items-start gap-3">
            <AlertCircle className="text-amber-600 mt-0.5" size={20} />
            <div className="text-sm">
              <p className="font-medium text-amber-800 mb-1">Educational Use Disclaimer</p>
              <p className="text-amber-700">
                This platform uses simulated medical data and AI-generated content for educational purposes only. 
                All findings must be verified by qualified medical professionals. This system is not intended for clinical decision-making.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
